import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Si estás usando react-router

const Protected = ({ tokens, setTokens }) => {
  const [message, setMessage] = useState('');
  const history = useHistory(); // Usar el hook useHistory para redirigir

  const fetchProtectedData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/private', {
        headers: {
          Authorization: `Bearer ${tokens.accessToken}`
        }
      });
      setMessage(response.data.message);
    } catch (error) {
      if (error.response.status === 401) {
        try {
          const refreshResponse = await axios.post('http://localhost:5000/api/auth/refresh-token', {
            token: tokens.refreshToken
          });
          const { accessToken } = refreshResponse.data;
          setTokens({ ...tokens, accessToken });
        } catch (refreshError) {
          console.error('Error al refrescar el token', refreshError);
        }
      } else {
        console.error(error);
      }
    }
  };

  const handleLogout = () => {
    setTokens({ accessToken: '', refreshToken: '' }); // Limpiar los tokens
    history.push('/login'); // Redirigir a la página de inicio de sesión
  };

  useEffect(() => {
    fetchProtectedData();
  }, [tokens]);

  return (
    <div>
      <h2>Ruta protegida</h2>
      <p>{message}</p>
      <button onClick={handleLogout}>Cerrar Sesión</button>
    </div>
  );
};

export default Protected;
