import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Protected = ({ tokens, setTokens }) => {
  const [message, setMessage] = useState('');

  const fetchProtectedData = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/private', {
        headers: {
          Authorization: `Bearer ${tokens.accessToken}`
        }
      });
      setMessage(response.data.message);
    } catch (error) {
      if (error.response.status === 401) {
        try {
          const refreshResponse = await axios.post('http://localhost:3000/api/auth/refresh-token', {
            token: tokens.refreshToken
          });
          setTokens({ ...tokens, accessToken: refreshResponse.data.accessToken });
        } catch (refreshError) {
          console.error('Refresh token failed', refreshError);
        }
      } else {
        console.error(error);
      }
    }
  };

  useEffect(() => {
    fetchProtectedData();
  }, [tokens]);

  return (
    <div>
      <h2>Protected</h2>
      <p>{message}</p>
    </div>
  );
};

export default Protected;
